import React, {Component} from 'react';
import axios from 'axios'
import { Container, Row, Col, Pagination, PaginationItem, PaginationLink } from 'reactstrap';
import CurrentJobPostingCards from "./CurrentJobPostingCards";
class JobView extends Component {

	ip = window.location.host.substr(0,window.location.host.indexOf(":"));


	state = {
		jobs: null
	};

	componentDidMount(){
		this.getJobs();
	}

	getJobs = async () => {
		let res = await axios.get("http://"+this.ip+":3001/getData");
		let {data} = await res.data;
		console.log(data);
		this.setState({jobs: data})
	};

	render() {
		if (this.state.jobs) {

			return (
				<Container fluid>
					<React.Fragment>
						<CurrentJobPostingCards jobs={this.state.jobs}/>
					</React.Fragment>
				</Container>
			)
		}

		return <div>Loading...</div>;
	}

}

export default JobView;
